import React, { useEffect, useState } from 'react';
import Draggable from 'react-draggable';
import './VistaPrevioVentana.css';

const VistaPrevioVentana = ({ tipo, onClose, datos }) => {
  const [formData, setFormData] = useState({
    id: '',
    foto1: '',
    local: ['', '', '', '', '', '', ''],
    titulo: ['', '', '', '', '', '', ''],
    colores: ['', '', '', '', '', '', ''],
    tituloGeneral: '',
  });

  useEffect(() => {
    if (datos) {
      setFormData({
        id: datos.id || '',
        foto1: datos.foto1 || '',
        local: datos.local || ['', '', '', '', '', '', ''],
        titulo: datos.titulo || ['', '', '', '', '', '', ''],
        colores: datos.colores || ['', '', '', '', '', '', ''],
        tituloGeneral: datos.tituloGeneral || '',
      });
    }
  }, [datos]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSave = () => {
    // L贸gica para guardar los datos
    console.log('Datos guardados:', formData);
    onClose();
  };

  const handleClose = () => {
    setFormData({
      id: '',
      foto1: '',
      local: ['', '', '', '', '', '', ''],
      titulo: ['', '', '', '', '', '', ''],
      colores: ['', '', '', '', '', '', ''],
      tituloGeneral: '',
    });
    onClose();
  };

  return (
    <Draggable handle=".ventana-edicion-header">
      <div className="ventana-edicion">
        <div className="ventana-edicion-contenido">
          <div className="ventana-edicion-header">
            <button className="ventana-edicion-cerrar" onClick={handleClose}>X</button>
            <h2>{tipo === 'Placa' ? 'Edici贸n de Placa' : 'Edici贸n de Z贸calo'}</h2>
          </div>
          {tipo === 'Placa' ? (
            <div className="ventana-edicion-fondo-container">
              <video className="ventana-edicion-fondo" autoPlay loop muted>
                <source src="/video/FONDO 02 DCSHA.mp4" type="video/mp4" />
                Your browser does not support the video tag.
              </video>
              <div className="ventana-edicion-form">
                <label>
                  ID:
                  <input
                    type="text"
                    name="id"
                    value={formData.id}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  Foto1:
                  <input
                    type="text"
                    name="foto1"
                    value={formData.foto1}
                    onChange={handleInputChange}
                  />
                </label>
                {formData.local.map((value, index) => (
                  <label key={`local-${index}`}>
                    Local {index + 1}:
                    <input
                      type="text"
                      name={`local-${index}`}
                      value={value}
                      onChange={(e) => {
                        const newLocal = [...formData.local];
                        newLocal[index] = e.target.value;
                        setFormData({ ...formData, local: newLocal });
                      }}
                    />
                  </label>
                ))}
                {formData.titulo.map((value, index) => (
                  <label key={`titulo-${index}`}>
                    T铆tulo {index + 1}:
                    <input
                      type="text"
                      name={`titulo-${index}`}
                      value={value}
                      onChange={(e) => {
                        const newTitulo = [...formData.titulo];
                        newTitulo[index] = e.target.value;
                        setFormData({ ...formData, titulo: newTitulo });
                      }}
                    />
                  </label>
                ))}
                <label>
                  Colores:
                  <select
                    name="colores"
                    value={formData.colores}
                    onChange={handleInputChange}
                  >
                    <option value="NORMAL">NORMAL</option>
                    <option value="DESTACADO">DESTACADO</option>
                  </select>
                </label>
                <label>
                  T铆tulo General:
                  <input
                    type="text"
                    name="tituloGeneral"
                    value={formData.tituloGeneral}
                    onChange={handleInputChange}
                  />
                </label>
                <button onClick={handleSave}>Salvar</button>
              </div>
            </div>
          ) : (
            <div className="ventana-edicion-form">
              <label>
                T铆tulo:
                <input
                  type="text"
                  name="titulo"
                  value={formData.titulo}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Descripci贸n:
                <textarea
                  name="descripcion"
                  value={formData.descripcion}
                  onChange={handleInputChange}
                />
              </label>
              <button onClick={handleSave}>Salvar</button>
            </div>
          )}
        </div>
      </div>
    </Draggable>
  );
};

export default VistaPrevioVentana;
