import React, { useEffect } from 'react';
import './VistaPrevioVentana.css';

const VistaPrevioVentana = ({ tipo, interfazZocalos, interfazPlacas, onClose }) => {
  useEffect(() => {
    if (tipo === 'Zocalo') {
      interfazZocalos.mostrarZocalos();
    } else if (tipo === 'Placa') {
      interfazPlacas.mostrarPlacas();
    }
  }, [tipo, interfazZocalos, interfazPlacas]);

  return (
    <div className="ventana-edicion">
      <div className="ventana-edicion-contenido">
        <button className="ventana-edicion-cerrar" onClick={onClose}>X</button>
        {tipo === 'Placa' ? (
          <video className="ventana-edicion-fondo" autoPlay loop muted>
            <source src="/video/FONDO 02 DCSHA.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        ) : (
          <div className="ventana-edicion-zocalo">
            <h2>Edición de Zócalo</h2>
            {/* Aquí puedes agregar el contenido específico para Zócalos */}
          </div>
        )}
      </div>
    </div>
  );
};

export default VistaPrevioVentana;
