import React from 'react';
import { Eye, Plus, Trash2, Download, Copy } from 'lucide-react';

const GestionAcciones = ({ element, toggleZcPl, addElement, removeElement, duplicarFila }) => {
  return (
    <div className="actions-container">
      {element.type !== 'Tiempo de Corte' && element.type !== 'Bloque' && element.type !== 'Total' && (
        <>
          <button className="add-link" onClick={addElement}><Plus /> </button>
          <button className="zcpl-link" onClick={() => toggleZcPl(element.id)}><Download /> ZcPl</button>
          <button className="view-link"><Eye /> View</button>
          <button className="delete-link" onClick={() => removeElement(element.id)}><Trash2 /> </button>
        </>
      )}
      {(element.type === 'Bloque' || element.type === 'Total') && (
        <button className="duplicate-link" onClick={() => duplicarFila(element.id)}><Copy /> Duplicar</button>
      )}
    </div>
  );
};

export default GestionAcciones;
