const fs = require('fs');
const path = require('path');

class InterfazZocalos {
    mostrarZocalos(tipo) {
        let filePath = '';
        switch (tipo) {
            case 'TITULOS':
                filePath = path.join(__dirname, 'VistaZócaloTítulo.html');
                break;
            // Agregar más casos para otros tipos de zócalos
            default:
                throw new Error(`Tipo de zócalo no soportado: ${tipo}`);
        }

        try {
            const htmlContent = fs.readFileSync(filePath, 'utf-8');
            return htmlContent;
        } catch (error) {
            console.error('Error al cargar el contenido HTML:', error);
            return '';
        }
    }
}

module.exports = InterfazZocalos;
