import React, { useEffect, useState } from 'react';
import Draggable from 'react-draggable';
import './VistaPrevioVentana.css';

const VistaPrevioVentana = ({ tipo, onClose, datos }) => {
  const [formData, setFormData] = useState({
    id: '',
    foto1: '',
    local: ['', '', '', '', '', '', ''],
    titulo: ['', '', '', '', '', '', ''],
    colores: ['', '', '', '', '', '', ''],
    tituloGeneral: '',
  });

  useEffect(() => {
    if (datos) {
      setFormData({
        id: datos.id || '',
        foto1: datos.foto1 || '',
        local: datos.local || ['', '', '', '', '', '', ''],
        titulo: datos.titulo || ['', '', '', '', '', '', ''],
        colores: datos.colores || ['', '', '', '', '', '', ''],
        tituloGeneral: datos.tituloGeneral || '',
      });
    }
  }, [datos]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleArrayChange = (e, index, field) => {
    const newArray = [...formData[field]];
    newArray[index] = e.target.value;
    setFormData({
      ...formData,
      [field]: newArray,
    });
  };

  const handleSave = () => {
    // Lógica para guardar los datos
    console.log('Datos guardados:', formData);
    onClose();
  };

  return (
    <Draggable handle=".ventana-edicion-header">
      <div className="ventana-edicion">
        <div className="ventana-edicion-contenido">
          <div className="ventana-edicion-header">
            <button className="ventana-edicion-cerrar" onClick={onClose}>X</button>
            <h2>{tipo === 'Placa' ? 'Edición de Placa' : 'Edición de Zócalo'}</h2>
          </div>
          {tipo === 'DATOS' ? (
            <div className="ventana-edicion-fondo-container">
              <img className="ventana-edicion-fondo" src="/images/P DATOS + FOTO.png" alt="Fondo DATOS" />
              <div className="ventana-edicion-form">
                <label>
                  ID:
                  <input
                    type="text"
                    name="id"
                    value={formData.id}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  Foto1:
                  <input
                    type="text"
                    name="foto1"
                    value={formData.foto1}
                    onChange={handleInputChange}
                  />
                </label>
                {formData.local.map((value, index) => (
                  <label key={`local-${index}`}>
                    Local {index + 1}:
                    <input
                      type="text"
                      name={`local-${index}`}
                      value={value}
                      onChange={(e) => handleArrayChange(e, index, 'local')}
                    />
                  </label>
                ))}
                {formData.titulo.map((value, index) => (
                  <label key={`titulo-${index}`}>
                    Título {index + 1}:
                    <input
                      type="text"
                      name={`titulo-${index}`}
                      value={value}
                      onChange={(e) => handleArrayChange(e, index, 'titulo')}
                    />
                  </label>
                ))}
                {formData.colores.map((value, index) => (
                  <label key={`colores-${index}`}>
                    Colores {index + 1}:
                    <select
                      name={`colores-${index}`}
                      value={value}
                      onChange={(e) => handleArrayChange(e, index, 'colores')}
                    >
                      <option value="NORMAL">NORMAL</option>
                      <option value="DESTACADO">DESTACADO</option>
                    </select>
                  </label>
                ))}
                <label>
                  Título General:
                  <input
                    type="text"
                    name="tituloGeneral"
                    value={formData.tituloGeneral}
                    onChange={handleInputChange}
                  />
                </label>
                <button onClick={handleSave}>Salvar</button>
              </div>
            </div>
          ) : (
            <div className="ventana-edicion-form">
              <label>
                Título:
                <input
                  type="text"
                  name="titulo"
                  value={formData.titulo}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Descripción:
                <textarea
                  name="descripcion"
                  value={formData.descripcion}
                  onChange={handleInputChange}
                />
              </label>
              <button onClick={handleSave}>Salvar</button>
            </div>
          )}
        </div>
      </div>
    </Draggable>
  );
};

export default VistaPrevioVentana;
