class SAL {
  static async almacenarPaginas(paginas) {
    if (typeof window !== 'undefined' && window.localStorage) {
      // Entorno del navegador
      localStorage.setItem('paginas', JSON.stringify(paginas));
    } else {
      // Entorno del servidor
      const fs = await import('fs').then(module => module.default);
      const path = await import('path').then(module => module.default);
      const filePath = path.join(__dirname, 'paginas.txt');
      fs.writeFileSync(filePath, JSON.stringify(paginas, null, 2), 'utf-8');
    }
  }

  static async obtenerPaginas() {
    if (typeof window !== 'undefined' && window.localStorage) {
      // Entorno del navegador
      const paginas = localStorage.getItem('paginas');
      return paginas ? JSON.parse(paginas) : [];
    } else {
      // Entorno del servidor
      const fs = await import('fs').then(module => module.default);
      const path = await import('path').then(module => module.default);
      const filePath = path.join(__dirname, 'paginas.txt');
      if (fs.existsSync(filePath)) {
        const paginas = fs.readFileSync(filePath, 'utf-8');
        return JSON.parse(paginas);
      }
      return [];
    }
  }
}

export default SAL;
