import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { Plus, LogIn, Upload } from 'lucide-react';
import './DCSHA.css'; // Importar la hoja de estilos
const Escaleta = require('../../Dominio/ModelosDeDominio/Escaleta'); // Asegúrate de que la ruta sea correcta

/**
 * Clase DCSHA
 * Esta clase maneja la gestión de plantillas y escaletas en la aplicación.
 */
class DCSHA {
    /**
     * Constructor de la clase DCSHA
     * Inicializa las propiedades de la clase.
     */
    constructor() {
        this.escaleta = new Escaleta(); // Crear una instancia de la clase Escaleta
    }

    /**
     * Método agregarFila
     * Método para agregar una nueva fila a la escaleta.
     * @param {object} fila - Objeto que representa la fila a agregar.
     */
    agregarFila(fila) {
        this.escaleta.agregarFila(fila);
    }
}

/**
 * Componente DCSHAComponent
 * Este componente maneja la gestión de plantillas y escaletas en la aplicación.
 */
function DCSHAComponent() {
  const history = useHistory();
  const [pagina, setPagina] = useState('');
  const [tipo, setTipo] = useState('');
  const [tiempoInicio, setTiempoInicio] = useState('');
  const [duracion, setDuracion] = useState('');
  const [tiempoEjecutado, setTiempoEjecutado] = useState('');

  const handleIngresarClick = () => {
    history.push('/vista-plantillas');
  };

  const handleAgregarFilaClick = () => {
    const fila = { pagina, tipo, tiempoInicio, duracion, tiempoEjecutado };
    const dcsha = new DCSHA();
    dcsha.agregarFila(fila);
  };

  return (
    <div className="dcsha-container">
      <div className="dcsha-header">
        <h1 className="dcsha-title">Programas</h1>
        <button className="dcsha-button">
          <Plus className="dcsha-button-icon" />
          Crear nuevo
        </button>
      </div>
      <div className="dcsha-content">
        <h2 className="dcsha-subtitle">DCSHA</h2>
        <p className="dcsha-description">De ciclismo se habla</p>
        <div className="dcsha-action-buttons">
          <button
            className="dcsha-action-button"
            onClick={handleIngresarClick}
          >
            <LogIn className="dcsha-action-button-icon" />
            Ingresar
          </button>
          <button className="dcsha-action-button" onClick={handleAgregarFilaClick}>
            <Upload className="dcsha-button-icon" />
            Agregar fila
          </button>
        </div>
        <div className="dcsha-form">
          <input
            type="text"
            placeholder="Página"
            value={pagina}
            onChange={(e) => setPagina(e.target.value)}
          />
          <input
            type="text"
            placeholder="Tipo"
            value={tipo}
            onChange={(e) => setTipo(e.target.value)}
          />
          <input
            type="text"
            placeholder="Tiempo de inicio"
            value={tiempoInicio}
            onChange={(e) => setTiempoInicio(e.target.value)}
          />
          <input
            type="text"
            placeholder="Duración"
            value={duracion}
            onChange={(e) => setDuracion(e.target.value)}
          />
          <input
            type="text"
            placeholder="Tiempo ejecutado"
            value={tiempoEjecutado}
            onChange={(e) => setTiempoEjecutado(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
}

export { DCSHA, DCSHAComponent };
