import React, { useEffect, useState } from 'react';
import './VistaPrevioVentana.css';

const VistaPrevioVentana = ({ tipo, onClose }) => {
  const [formData, setFormData] = useState({
    titulo: '',
    descripcion: '',
    // Agrega más campos según sea necesario
  });

  useEffect(() => {
    // Aquí puedes agregar cualquier lógica adicional que necesites al montar el componente
  }, [tipo]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSave = () => {
    // Lógica para guardar los datos
    console.log('Datos guardados:', formData);
    onClose();
  };

  return (
    <div className="ventana-edicion">
      <div className="ventana-edicion-contenido">
        <button className="ventana-edicion-cerrar" onClick={onClose}>X</button>
        {tipo === 'Placa' ? (
          <div className="ventana-edicion-fondo">
            <video className="ventana-edicion-video" autoPlay loop muted>
              <source src="/video/FONDO 02 DCSHA.mp4" type="video/mp4" />
              Your browser does not support the video tag.
            </video>
            <div className="ventana-edicion-form">
              <h2>Edición de Placa</h2>
              <label>
                Título:
                <input
                  type="text"
                  name="titulo"
                  value={formData.titulo}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Descripción:
                <textarea
                  name="descripcion"
                  value={formData.descripcion}
                  onChange={handleInputChange}
                />
              </label>
              {/* Agrega más campos según sea necesario */}
              <button onClick={handleSave}>Salvar</button>
            </div>
          </div>
        ) : (
          <div className="ventana-edicion-form">
            <h2>Edición de Zócalo</h2>
            <label>
              Título:
              <input
                type="text"
                name="titulo"
                value={formData.titulo}
                onChange={handleInputChange}
              />
            </label>
            <label>
              Descripción:
              <textarea
                name="descripcion"
                value={formData.descripcion}
                onChange={handleInputChange}
              />
            </label>
            {/* Agrega más campos según sea necesario */}
            <button onClick={handleSave}>Salvar</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default VistaPrevioVentana;
