import React, { useEffect, useState } from 'react';
import Draggable from 'react-draggable';
import './VistaPrevioVentana.css';

const VistaPrevioVentana = ({ tipo, onClose, datos }) => {
  const [formData, setFormData] = useState({
    id: '',
    foto1: '',
    local: ['', '', '', '', '', '', ''],
    titulo: ['', '', '', '', '', '', ''],
    colores: ['', '', '', '', '', '', ''],
    tituloGeneral: '',
  });

  useEffect(() => {
    if (datos) {
      setFormData({
        id: datos.id || '',
        foto1: datos.foto1 || '',
        local: datos.local || ['', '', '', '', '', '', ''],
        titulo: datos.titulo || ['', '', '', '', '', '', ''],
        colores: datos.colores || ['', '', '', '', '', '', ''],
        tituloGeneral: datos.tituloGeneral || '',
      });
    }
  }, [datos]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSave = () => {
    // L贸gica para guardar los datos
    console.log('Datos guardados:', formData);
    onClose();
  };

  return (
    <Draggable handle=".ventana-edicion-header">
      <div className="ventana-edicion">
        <div className="ventana-edicion-contenido">
          <div className="ventana-edicion-header">
            <div className="ventana-edicion-titulo">
              <h2>{tipo === 'Placa' ? 'Edici贸n de Placa' : 'Edici贸n de Z贸calo'}</h2>
            </div>
            <div className="ventana-edicion-controles">
              <button className="ventana-edicion-minimizar">_</button>
              <button className="ventana-edicion-maximizar">[]</button>
              <button className="ventana-edicion-cerrar" onClick={onClose}>X</button>
            </div>
          </div>
          {tipo === 'Placa' ? (
            <div className="ventana-edicion-fondo-container">
              <video className="ventana-edicion-fondo" autoPlay loop muted>
                <source src="/video/FONDO 02 DCSHA.mp4" type="video/mp4" />
                Your browser does not support the video tag.
              </video>
              {datos.tipo === 'DATOS' && (
                <img src="/images/P DATOS + FOTO.png" alt="Fondo Datos" className="ventana-edicion-fondo-medio" />
              )}
              <div className="ventana-edicion-form">
                <label>
                  ID:
                  <input
                    type="text"
                    name="id"
                    value={formData.id}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  Foto1:
                  <input
                    type="text"
                    name="foto1"
                    value={formData.foto1}
                    onChange={handleInputChange}
                  />
                </label>
                <label>
                  Local:
                  {formData.local.map((local, index) => (
                    <input
                      key={index}
                      type="text"
                      name={`local${index}`}
                      value={local}
                      onChange={(e) => handleInputChange(e, index)}
                    />
                  ))}
                </label>
                <label>
                  T铆tulo:
                  {formData.titulo.map((titulo, index) => (
                    <input
                      key={index}
                      type="text"
                      name={`titulo${index}`}
                      value={titulo}
                      onChange={(e) => handleInputChange(e, index)}
                    />
                  ))}
                </label>
                <label>
                  Colores:
                  <select
                    name="colores"
                    value={formData.colores}
                    onChange={handleInputChange}
                  >
                    <option value="NORMAL">NORMAL</option>
                    <option value="DESTACADO">DESTACADO</option>
                  </select>
                </label>
                <label>
                  T铆tulo General:
                  <input
                    type="text"
                    name="tituloGeneral"
                    value={formData.tituloGeneral}
                    onChange={handleInputChange}
                  />
                </label>
                <button onClick={handleSave}>Salvar</button>
              </div>
            </div>
          ) : (
            <div className="ventana-edicion-form">
              <label>
                T铆tulo:
                <input
                  type="text"
                  name="titulo"
                  value={formData.titulo}
                  onChange={handleInputChange}
                />
              </label>
              <label>
                Descripci贸n:
                <textarea
                  name="descripcion"
                  value={formData.descripcion}
                  onChange={handleInputChange}
                />
              </label>
              <button onClick={handleSave}>Salvar</button>
            </div>
          )}
        </div>
      </div>
    </Draggable>
  );
};

export default VistaPrevioVentana;
