import ZocaloTitulo from './ZocaloTitulo';
import ZocaloCatastrofe from './ZocaloCatastrofe';
import ZocaloDosLineas from './ZocaloDosLineas';
import ZocaloVentas from './ZocaloVentas';
import ZocaloTextuales from './ZocaloTextuales';
import Equipo from './Equipo';
import DatosEquipos from './DatosEquipos';
import PventasX1 from './PventasX1';

class AsistenteProduccion {
  constructor() {
    this.elements = [];
  }

  crearCelda(id, type, name, startTime, duration, elapsedTime) {
    const newElement = {
      id,
      type,
      name,
      startTime,
      duration,
      elapsedTime,
      zocalos: [],
      placas: [],
    };
    this.elements.push(newElement);
    return this.elements;
  }

  insertarZocalo(id, zocalo) {
    const element = this.elements.find(el => el.id === id);
    if (element) {
      element.zocalos.push(zocalo);
    }
    return this.elements;
  }

  insertarPlaca(id, placa) {
    const element = this.elements.find(el => el.id === id);
    if (element) {
      element.placas.push(placa);
    }
    return this.elements;
  }

  // Métodos específicos para insertar diferentes tipos de zócalos
  insertarZocaloTitulo(id) {
    const zocalo = new ZocaloTitulo();
    return this.insertarZocalo(id, zocalo);
  }

  insertarZocaloCatastrofe(id) {
    const zocalo = new ZocaloCatastrofe();
    return this.insertarZocalo(id, zocalo);
  }

  insertarZocaloDosLineas(id) {
    const zocalo = new ZocaloDosLineas();
    return this.insertarZocalo(id, zocalo);
  }

  insertarZocaloVentas(id) {
    const zocalo = new ZocaloVentas();
    return this.insertarZocalo(id, zocalo);
  }

  insertarZocaloTextuales(id) {
    const zocalo = new ZocaloTextuales();
    return this.insertarZocalo(id, zocalo);
  }

  // Métodos específicos para insertar diferentes tipos de placas
  insertarPlacaEquipos(id) {
    const placa = new Equipo();
    return this.insertarPlaca(id, placa);
  }

  insertarPlacaDatosEquipos(id) {
    const placa = new DatosEquipos();
    return this.insertarPlaca(id, placa);
  }

  insertarPlacaComparativa(id) {
    const placa = new Placa(); // Asumiendo que hay una clase `Placa` para comparativa
    return this.insertarPlaca(id, placa);
  }

  insertarPlacaDatos(id) {
    const placa = new Placa(); // Asumiendo que hay una clase `Placa` para datos
    return this.insertarPlaca(id, placa);
  }

  insertarPlacaPventaX3(id) {
    const placa = new PventasX1(); // Asumiendo que `PventasX1` puede manejar `PVENTA X 3`
    return this.insertarPlaca(id, placa);
  }

  insertarPlacaPventaX1(id) {
    const placa = new PventasX1();
    return this.insertarPlaca(id, placa);
  }
}

export default AsistenteProduccion;
